import React from "react";

function Result({ score, answers, onRestart }) {
  return (
    <div className="result">
      <h2>Your Score: {score} / {answers.length}</h2>
      <ul>
        {answers.map((ans, idx) => (
          <li key={idx}>
            <p><strong>{ans.question}</strong></p>
            <p>Your Answer: {ans.selected}</p>
            <p>Correct Answer: {ans.correctAnswer}</p>
            <p>{ans.isCorrect ? "✅ Correct" : "❌ Incorrect"}</p>
          </li>
        ))}
      </ul>
      <button onClick={onRestart}>Restart Quiz</button>
    </div>
  );
}

export default Result;
